                    <br></br>
                    <h2>{question}</h2>
                    {
                        options.map((option, index) => (  //for each option, new paragrap
                            <div key={index} className={`options-container`}>
                                <p className="options-text">{option}</p>
                                <input className="OwnInput" type="number" id={index} name="quantity" min="1" ></input>
