import { withStyles, createTheme } from "@material-ui/core/styles";
import LinearProgress from "@material-ui/core/LinearProgress";
import Grid from "@material-ui/core/Grid"
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import CircularProgress from '@material-ui/core/CircularProgress';

import { QuizData } from './Data/Fragen';

export const theme = createTheme({
    root: {
        flexGrow:1,
    },
    palette: {
        primary: {
            main: '#66bb6a',
            contrastText: '#fff',
        },
        secondary: { main: "#F1B929" }, // This is just green.A700 as hex.
    },
    overrides: {
        MuiButton: {
            root:{
                backgroundColor: 'primary',
            text: {
                borderRadius: 3,
                border: 0,
                color: 'white',
            },
        }
        },
    }
});

export const BorderLinearProgress = withStyles((theme) => ({
    root: {
        height: 20,
        width:'80%',
        borderRadius: 10,
        justifyContent: 'center'
    },
    colorPrimary: {
        backgroundColor:
            theme.palette.grey[theme.palette.type === "light" ? 200 : 700]
    },
    bar: {
        borderRadius: 5,
        backgroundColor: "primary"
    }
}))(LinearProgress);

export function ScoreHeader(props) {
    return (
        <div>
            <Grid container maxwidth="false" align="center" justifyContent="center" alignItems="center" >
                <Grid item xs={6} sm={4} md={3} lg={2} xl={1} >
                    <BorderLinearProgress variant="determinate" value={(props.currentIndex / QuizData.length) * 100} />
                </Grid>
                <Grid item xs={6} sm={4} md={3} lg={2} xl={1}>
                    <Typography variant="h5" color="primary" gutterBottom>
                        Score
                    </Typography>
                    <Typography variant="h3" color="primary" gutterBottom>
                        {props.score}
                    </Typography>
                </Grid>
            </Grid>
            <br></br>
        </div>

    );
}


export function CircularProgressWithLabel(props) {
    return (
      <Box position="relative"  display="inline-flex">
        <CircularProgress variant="static" size="10%" color="primary"  {...props}/>
        <Box
          top={0}
          left={0}
          bottom={0}
          right={0}
          position="absolute"
          display="flex"
          alignItems="center"
          justifyContent="center"          
        >
          <Typography variant="caption" component="div" color="textSecondary">{`${Math.round(
            props.value,
          )}%`}</Typography>
        </Box>
      </Box>
    );
  }


