import React, { Component } from 'react'
import { QuizData } from './Data/Fragen';
import './circle.css';
//import {initialize, notFound, foundYou, calcRoute} from './Maps/maps';
import { LoadScript } from "@react-google-maps/api";
import Map from "./Maps/maps";

import Singlequestion from './SingleQuestion/singleQuestion';
import QuizEnds from './Score/quizEnd';
import Number from './Number/Number';

const lib = ["places"];
const key = "AIzaSyAF6K0mCOn8PxRcsTPBapCtXyr_zZ6OWGk"; // PUT GMAP API KEY HERE

class Quiz extends Component {

    state = {
        userAnswer: null,    //current users answer
        type: "single",         //question Type
        currentIndex: 0,     //current questions index
        options: [],        //the options
        values: null,         //Values of Single or Multiple choice
        tipps: null,
        ChoosenTipps: [],
        quizEnd: false,
        score: 0,
        distance: 0,
        TravelMode: 'Car',
        co2: 0,

        adults: 0,
        children: 0,

        num_backpags:0,
        kilo_backpags: 0,


        directions: null,
        bounds: null
    }


    //Component that holds the current quiz
    loadQuiz = () => {
        const { currentIndex } = this.state //get the current index
        this.setState(() => {
            return {
                question: QuizData[currentIndex].question,
                type: QuizData[currentIndex].type,
                options: QuizData[currentIndex].options,
                values: QuizData[currentIndex].values,
                tipps: QuizData[currentIndex].tipps,
                distance: 0
            }
        }
        )
    }

    static setDistance = (distanceKM) => {
        this.setState({
            distance: distanceKM
        })
    }

    //Handles Click event for the next button
    nextQuestionHander = () => {
        const { userAnswer, score } = this.state
        this.setState({
            score: score + userAnswer,
            currentIndex: this.state.currentIndex + 1
        })
    }

    afterMap = () => {
        //var distanceKM = document.getElementById("Distance").value;
        //console.log(distanceKM)
        console.log(this.state.distance);
        this.setState({
            currentIndex: this.state.currentIndex + 1,
            //distance: distanceKM
        })
    }

    //Load the quiz once the component mounts
    componentDidMount() {
        this.loadQuiz();
    }

    //Update the component
    componentDidUpdate(prevProps, prevState) {
        const { currentIndex } = this.state;
        if (this.state.currentIndex !== prevState.currentIndex) {
            this.setState(() => {
                return {
                    type: QuizData[currentIndex].type,
                    question: QuizData[currentIndex].question,
                    options: QuizData[currentIndex].options,
                    values: QuizData[currentIndex].values

                }
            });

        }
    }/*componentDidUpdate*/


    callbackSingleQuestion(answer, points, Travelmode){
        const {score, currentIndex} = this.state;
        if(Travelmode === 'None'){
            this.setState({
                score: this.state.score + points,
            })
        }else{
            this.setState({
                Travelmode: Travelmode
            })
        }

        this.state.ChoosenTipps.push(QuizData[currentIndex].tipps[answer]);

        if (currentIndex === QuizData.length - 1) {
            this.setState({
                quizEnd: true
            })
        } else {
            this.setState({
                currentIndex: this.state.currentIndex + 1
            })
        }
    }

    callbackPeople(Adults, Childs){

        this.setState({
            adults: Adults,
            children: Childs
        })
    }

    /*
    checkNumber = (index) => {
        const { currentIndex } = this.state;
        console.log(currentIndex);


        this.setState({
            disabled: false,
            currentIndex: this.state.currentIndex + 1
        })
    }
*/
    //Responds to the click of the finish button
    finishHandler = () => {
        if (this.state.currentIndex === QuizData.length - 1) {
            this.setState({
                quizEnd: true
            })
        }

    }


    render() {
        const {quizEnd, type } = this.state //get the current state       
        if (quizEnd) {
            return (
                <div>
                    <QuizEnds state={this.state}/>
                </div>
            )
        }

        if(type === "single") {
            return(
                <Singlequestion state={this.state} callbackSingleQuestion={this.callbackSingleQuestion}/>
            )
        } else if (type === "number") {
            return (
                <div>
                    <Number state={this.state}/>                
                </div >
                )
        } else if (type === "transport") {
            return (
                <div>
                    <LoadScript googleMapsApiKey={key} libraries={lib}>
                        <Map score={this.state.score} currentIndex={this.state.currentIndex} question={this.state.question}/>
                    </LoadScript>
                    <br></br>
                    <div className="OwnSubmit"
                            onClick={() => this.afterMap()}>
                        Submit</div>
            </div > )
        }
    }/*render*/

}/*Quiz*/


export default Quiz;
