    const { question, options, currentIndex, score } = this.props.state;
    return (
      <div>
        <ScoreHeader score={score} currentIndex={currentIndex} />
        <Grid container maxwidth="false" align="center" justifyContent="center" alignItems="center" >
          <Grid item xs={12} sm={12} md={6} lg={4}
            style={{
              textAlign: 'center',
              align: 'center',
              justifyContent: 'center',
              alignItems: 'center',
              width: '80%',
              margin: 'center'
            }}
          >
            <FormControl component="fieldset" >
              <FormLabel component="legend" aligncontent="center">{this.props.state.question}</FormLabel>
              <br></br>
              <Autocomplete
                id="origin"
                options={airports}

                getOptionLabel={(option) => option.name}
                style={{ width: 300 }}
                renderInput={(params) => <TextField {...params} label="Origin" variant="outlined" />}
              />
              <br></br>
              <Autocomplete
                id="destination"
                options={airports}
                getOptionLabel={(option) => option.name + option.code}
                style={{ width: 300 }}
                renderInput={(params) => <TextField {...params} label="Destination" variant="outlined" />}
              />
              <ThemeProvider theme={theme}>
                <Button variant='contained' color='primary' onClick={this.getNumber}>
                  Next
                </Button>
              </ThemeProvider>
            </FormControl>
          </Grid>
        </Grid>

      </div >
    );
  }
}

export default Flight;
