import { makeStyles, withStyles } from "@material-ui/core/styles";
import LinearProgress from "@material-ui/core/LinearProgress";
import Grid from "@material-ui/core/Grid"
import Typography from '@material-ui/core/Typography';


import { QuizData } from './Data/Fragen';


export const BorderLinearProgress = withStyles((theme) => ({
    root: {
        height: 20,
        borderRadius: 10
    },
    colorPrimary: {
        backgroundColor:
            theme.palette.grey[theme.palette.type === "light" ? 200 : 700]
    },
    bar: {
        borderRadius: 5,
        backgroundColor: "#1a90ff"
    }
}))(LinearProgress);

export function ScoreHeader(props) {
    return (
        <div>
            <Grid container spacing={3} align="center" justify="center" alignItems="center" >
                <Grid item xs={6}>
                    <BorderLinearProgress variant="determinate" value={(props.currentIndex / QuizData.length) * 100} />
                </Grid>
                <Grid item xs={6}>
                    <Typography variant="h5" gutterBottom>
                        Score
                    </Typography>
                    <Typography variant="h3" gutterBottom>
                        {props.score}
                    </Typography>
                </Grid>
            </Grid>
            <br></br>
        </div>

    );
}

