/*
 * @Author: Florian Griesser 
 * @Date: 2021-05-29 10:29:35 
 * @Last Modified by: Florian Griesser
 * @Last Modified time: 2021-06-12 08:35:57
 */

import { Component } from "react";
import { carValues } from "../Data/Data";
import FormLabel from '@material-ui/core/FormLabel'
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import FormControl from '@material-ui/core/FormControl';
import { Box, ThemeProvider } from "@material-ui/core";
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import { FormControlLabel } from "@material-ui/core";
import { CircularProgress } from '@material-ui/core';


//import { QuizData } from '../Data/Fragen';
import { theme, CircularProgressWithLabel } from "../stylesUI";

import {
    CircularProgressbar,
    CircularProgressbarWithChildren,
    buildStyles
} from "react-circular-progressbar";
import "react-circular-progressbar/dist/styles.css";

// Animation
import { easeQuadInOut } from "d3-ease";
import AnimatedProgressProvider from "../Style/AnimatedProgressProvider";
import ChangingProgressProvider from "../Style/ChangingProgressProvider";


class QuizEnds extends Component {
    calculateCar = (distance, carPower, carType) => {
        //const {distance, carType, carPower} = this.props.state;

        var Factor = carValues[carPower][carType];



        return (Factor * distance).toFixed(0);
    }

    calculateTrain = () => {
        const { distance } = this.props.state;

        return (0.032 * distance).toFixed(0);
    }
    calculateFlight = (distance) => {

        return (0.369 * distance).toFixed(0);
    }

    calcScore = (co2) => {
        var score = 0;

        score = 75 - co2 * 75 / 2500;

        if (co2 > 2500) {
            score = 0;
        }

        return score;
    }

    compareCo2 = (TravelMode, co2) => {


    }


    render() {
        const { score, distance, ChoosenTipps, TravelMode, carPower, carType } = this.props.state;
        var co2 = 0;
        var EndScore = score;
        if (TravelMode === 'Car') {
            co2 = this.calculateCar(distance, carPower, carType);
        }
        else if (TravelMode === 'Train') {
            co2 = this.calculateTrain(distance);
        }
        else if (TravelMode === 'Fligth') {
            co2 = this.calculateFlight(distance);
        }

        EndScore = (EndScore + this.calcScore(co2)).toFixed(0);
        var circle = "c100 p" + EndScore + " big green";

        return (
            <div>
                <Grid container maxwidth="false" align="center" justifyContent="center" alignItems="center" >
                    <Typography variant="h4">Dein Holirate Rating ist fertig!</Typography>
                    <div style={{ padding: "20% 20% 20% 20%" }}>
                        <ChangingProgressProvider max-width="80%" values={[0, 20, 40, 60, 80]}>
                            {(value) => (
                                <CircularProgressbar
                                    value={value}
                                    text={`${value}%`}
                                    circleRatio={0.75}
                                    styles={buildStyles({
                                        rotation: 1 / 2 + 1 / 8,
                                        strokeLinecap: "butt",
                                        trailColor: "#eee",
                                        pathColor: "#66bb6a"
                                    })}
                                />
                            )}
                        </ChangingProgressProvider>
                    </div>
                    <Typography variant="h4">Kilometer: {distance}</Typography>
                    <Typography variant="h4">C02: {co2} kg</Typography>

                    <Grid container maxwidth="false" align="center" justifyContent="center" alignItems="center" >
                            <FormControl component="fieldset" >
                                <FormLabel component="legend" aligncontent="center">Hier noch ein paar Tipps f√ºr deine Reise:</FormLabel>
                                <Box name="quiz" value={this.indexValue} onChange={this.radioHandler}>
                                    {
                                        ChoosenTipps.map((tipp, index) => (  //for each option, new paragrap
                                            <FormControlLabel value={index + 1}
                                                key={index + 1}//for dumb unique key
                                                control={<Box
                                                    color="primary"
                                                />}
                                                label={tipp} />
                                        ))
                                    }
                                </Box>
                            </FormControl>
                        </Grid>
                    </Grid>
            </div>
        )
    };
}

export default QuizEnds;
