/*
 * @Author: Florian Griesser 
 * @Date: 2021-05-29 17:23:53 
 * @Last Modified by: Florian Griesser
 * @Last Modified time: 2021-05-29 17:35:49
 */

import React from "react";
import Radio from '@material-ui/core/Radio';
import Button from '@material-ui/core/Button';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormControl from '@material-ui/core/FormControl';
import FormLabel from '@material-ui/core/FormLabel'
import { makeStyles } from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid"


import { QuizData } from '../Data/Fragen';

const classes = makeStyles((theme) => ({
    root: {
      flexGrow: 1
    },
    paper: {
      padding: theme.spacing(2),
      textAlign: "center",
      color: "blue"
    }
  }));


  import LinearProgress from "@material-ui/core/LinearProgress";

  const BorderLinearProgress = withStyles((theme) => ({
    root: {
      height: 10,
      borderRadius: 5
    },
    colorPrimary: {
      backgroundColor:
        theme.palette.grey[theme.palette.type === "light" ? 200 : 700]
    },
    bar: {
      borderRadius: 5,
      backgroundColor: "#1a90ff"
    }
  }))(LinearProgress);


class Singlequestion extends React.Component {

    //Check the answer
    checkAnswer = (answer, index) => {
        const { currentIndex } = this.props.state;
        var Travelmode = null;
        var points = 0;
        console.log(index);

        if (QuizData[currentIndex].options[index] === "Flugzeug") {
            Travelmode = "Fligth";
        } else if (QuizData[currentIndex].options[index] === "Auto") {
            Travelmode = "Car";
        } else if (QuizData[currentIndex].options[index] === "Zug") {
            Travelmode = "Train";
        } else {
            points = QuizData[currentIndex].values[index];
        }


        this.props.callbackSingleQuestion(index, points, Travelmode);
    }/*checkAnswer*/




    render() {
        const { question, options, currentIndex, score } = this.props.state;

        return (
            <div>
                        <div id="progressBar">
                            <div id="progressBarFull" style={{ width: `${(currentIndex / QuizData.length) * 100}%` }}></div>
                        </div>

                <Grid container spacing={3}>
                    <Grid item xs={6}>
                        <Paper >Progress</Paper>
                    </Grid>
                    <Grid item xs={6}>

                        <Paper >Score</Paper>
                        <Paper >{score}</Paper>
                    </Grid>

                </Grid>
                <br></br>
                <br></br>
                <FormControl component="fieldset">
                    <FormLabel component="legend">{question}</FormLabel>
                    <RadioGroup aria-label="gender" name="gender1" >
                        {
                            options.map((option, index) => (  //for each option, new paragrap
                                <FormControlLabel value={index + 1}
                                    onChange={() => this.checkAnswer(option, index)}
                                    control={<Radio />}
                                    label={option} />
                            ))
                        }
                    </RadioGroup>
                <br></br>

                    <Button type="submit" variant="contained" color="secondary" >
                        Next
                    </Button>
                </FormControl>
            </div >
        )
    }
}

export default Singlequestion;
