// https://bryce.fisher-fleig.org/setting-up-istanbul-with-jasmine/

import Jasmine from 'jasmine'

try {
  require('./test-env.js')
} catch (ex) {
  // Silence is gold!
}

var ORSKEY = process.env.ORSKEY

if (ORSKEY && ORSKEY !== 'put-an-ors-key-here' && ORSKEY != '') {
  let jasmine = new Jasmine()

  // Modify this line to point to your jasmine.json
  jasmine.loadConfigFile('./spec/jasmine.json')

  // Some tests take longer than the default 5000ms of Jasmine
  jasmine.DEFAULT_TIMEOUT_INTERVAL = 30000
  jasmine.execute()
} else {
  // eslint-disable-next-line no-console
  console.log(
    '\x1b[33m%s\x1b[0m',
    'A valid ORSKEY was not set via spec/test-env.js file nor via environment variable. The tests were aborted.'
  )
  process.exit(1)
}

// Define the ORSKEY for local tests.
// The same variable is defined in travis-ci to run automated tests
if (!process.env.ORSKEY) {
  process.env.ORSKEY =
    '5b3ce3597851110001cf62484c2b303725d843b5b765b5e83e8e3c30'
}

/* eslint-disable no-undef */
import OrsDirections from '../src/OrsDirections'
import OrsInput from '../src/OrsInput'
import Constants from '../src/constants'

describe('No api key Directions Test', function() {
  it('Instantiate with no key results', function(done) {
    try {
      expect(function() {
        new OrsDirections({})
      }).toThrow(new Error(Constants.missingAPIKeyMsg))
      done()
    } catch (error) {
      done.fail('No api key Directions Test ' + error)
    }
  })
})

const orsDirections = new OrsDirections({ api_key: process.env.ORSKEY })

describe('Simple Route', function() {
  it('Get results', function(done) {
    orsDirections.clearPoints()
    orsDirections.addWaypoint(new OrsInput('8.690958', '49.404663').coord)
    orsDirections.addWaypoint(new OrsInput('8.687868', '49.390139').coord)

    orsDirections
      .calculate({
        profile: 'driving-car',
        format: 'json'
      })
      .then(function(json) {
        expect(json.routes.length).toBeGreaterThan(0)
        expect(json.routes[0].summary.distance).toBeGreaterThan(2300)
        expect(json.routes[0].summary.distance).toBeLessThan(2800)
        done()
      })
      .catch(function(err) {
        done.fail(err.message)
      })
  })

  it('Get results with custom host and service', function(done) {
    let customHostAndServiceDirections = new OrsDirections({
      api_key: process.env.ORSKEY,
      host: Constants.defaultHost,
      service: 'directions'
    })
    customHostAndServiceDirections.addWaypoint(
      new OrsInput('8.690958', '49.404663').coord
    )
    customHostAndServiceDirections.addWaypoint(
      new OrsInput('8.687868', '49.390139').coord
    )

    customHostAndServiceDirections
      .calculate({
        profile: 'driving-car',
        format: 'json'
      })
      .then(function(json) {
        expect(json.routes.length).toBeGreaterThan(0)
        expect(json.routes[0].summary.distance).toBeGreaterThan(2300)
        expect(json.routes[0].summary.distance).toBeLessThan(2800)
        done()
      })
      .catch(function(err) {
        done.fail(err.message)
      })
  })

  it('Clear directions args', function(done) {
    orsDirections.clear()
    try {
      expect(orsDirections.args.coordinates).toBeUndefined()
      done()
    } catch (error) {
      // eslint-disable-next-line no-console
      console.log(orsDirections.args)
      done.fail(
        'Should not fail Clear directions args Test ' + orsDirections.args
      )
    }
  })

  it('Compare Fastest vs. Shortest', function(done) {
    orsDirections.clearPoints()
    orsDirections.addWaypoint(new OrsInput('8.690958', '49.404662').coord)
    orsDirections.addWaypoint(new OrsInput('8.687868', '49.390139').coord)

    orsDirections
      .calculate({
        profile: 'driving-car',
        preference: 'fastest',
        format: 'json'
      })
      .then(function(json) {
        var fastestTime = json.routes[0].summary.duration
        var fastestDistance = json.routes[0].summary.distance
        // Shortest is not prepared with CH
        orsDirections
          .calculate({
            profile: 'driving-car',
            preference: 'shortest',
            avoidables: ['tollways', 'ferries', 'fords'],
            format: 'json'
          })
          .then(function(json2) {
            expect(json2.routes[0].summary.duration).toBeGreaterThan(
              fastestTime
            )
            expect(json2.routes[0].summary.distance).toBeLessThan(
              fastestDistance
            )
            done()
          })
          .catch(function(err) {
            done.fail(err.message)
          })
      })
      .catch(function(err) {
        done.fail(err.message)
      })
  })
})

import request from 'superagent'
import Promise from 'bluebird'
import OrsUtil from './OrsUtil'
import Constants from './constants'

const orsUtil = new OrsUtil()

class OrsDirections {
  constructor(args) {
    this.requestSettings = null
    this.args = {}
    this.meta = null
    if (Constants.propNames.apiKey in args) {
      this.args[Constants.propNames.apiKey] = args[Constants.propNames.apiKey]
    } else {
      // eslint-disable-next-line no-console
      console.error(Constants.missingAPIKeyMsg)
      throw new Error(Constants.missingAPIKeyMsg)
    }

    if (Constants.propNames.host in args) {
      this.args[Constants.propNames.host] = args[Constants.propNames.host]
    }
    if (Constants.propNames.service in args) {
      this.args[Constants.propNames.service] = args[Constants.propNames.service]
    }
  }

  clear() {
    for (let variable in this.args) {
      if (variable !== Constants.apiKeyPropName) delete this.args[variable]
    }
  }

  clearPoints() {
    if ('coordinates' in this.args) this.args.coordinates.length = 0
  }

  addWaypoint(latlon) {
    if (!('coordinates' in this.args)) {
      this.args.coordinates = []
    }
    this.args.coordinates.push(latlon)
  }

  getBody(args) {
    if (args.options && typeof args.options !== 'object') {
      args.options = JSON.parse(args.options)
    }

    // Set the default vehicle type when profile is 'driving-hgv' if it is missing
    // eslint-disable-next-line prettier/prettier
    if (this.meta && this.meta.profile === 'driving-hgv' && (!args.options || !args.options.vehicle_type)) {
      args.options = args.options || {}
      args.options.vehicle_type = 'hgv'
    }

    if (args.restrictions) {
      args.options = args.options || {}
      args.options.profile_params = {
        restrictions: { ...args.restrictions }
      }
      delete args.options.restrictions
    }

    if (args.avoidables) {
      args.options = args.options || {}
      args.options.avoid_features = [...args.avoidables]
      delete args.avoidables
    }
    return args
  }

  calculate(reqArgs) {
    // Get custom header and remove from args
    this.customHeaders = []
    if (reqArgs.customHeaders) {
      this.customHeaders = reqArgs.customHeaders
      delete reqArgs.customHeaders
    }
    orsUtil.setRequestDefaults(this.args, reqArgs, true)
    if (!this.args[Constants.propNames.service]) {
      this.args[Constants.propNames.service] = 'directions'
    }
    orsUtil.copyProperties(reqArgs, this.args)

    const that = this
    return new Promise(function(resolve, reject) {
      const timeout = that.args[Constants.propNames.timeout] || 10000
      // meta should be generated once that subsequent requests work
      if (that.meta == null) {
        that.meta = orsUtil.prepareMeta(that.args)
      }
      that.httpArgs = orsUtil.prepareRequest(that.args)
      let url = orsUtil.prepareUrl(that.meta)

      const postBody = that.getBody(that.httpArgs)
      let authorization = that.meta[Constants.propNames.apiKey]
      let orsRequest = request
        .post(url)
        .send(postBody)
        .set('Authorization', authorization)
        .timeout(timeout)
      // .accept(that.meta.mimeType)

      for (let key in that.customHeaders) {
        orsRequest.set(key, that.customHeaders[key])
      }
      orsRequest.end(function(err, res) {
        if (err || !res.ok) {
          // eslint-disable-next-line no-console
          console.error(err)
          reject(err)
        } else if (res) {
          resolve(res.body || res.text)
        }
      })
    })
  }
}

export default OrsDirections

import Constants from './constants'
class OrsUtil {
  copyProperties(args, argsInto) {
    if (!args) return argsInto

    for (var prop in args) {
      // eslint-disable-next-line no-prototype-builtins
      if (args.hasOwnProperty(prop) && args[prop] !== undefined) {
        argsInto[prop] = args[prop]
      }
    }
    return argsInto
  }

  prepareMeta(args) {
    return {
      host: args[Constants.propNames.host],
      api_version: args[Constants.propNames.apiVersion],
      profile: args[Constants.propNames.profile],
      format: args[Constants.propNames.format],
      service: args[Constants.propNames.service],
      api_key: args[Constants.propNames.apiKey],
      mime_type: args[Constants.propNames.mimeType]
    }
  }

  prepareRequest(args) {
    delete args[Constants.propNames.mimeType]
    delete args[Constants.propNames.host]
    delete args[Constants.propNames.apiVersion]
    delete args[Constants.propNames.service]
    delete args[Constants.propNames.apiKey]
    delete args[Constants.propNames.profile]
    delete args[Constants.propNames.format]
    delete args[Constants.propNames.timeout]
    return { ...args }
  }

  /**
   * Prepare the request url based on url constituents
   * @param {Objet} args
   */
  prepareUrl(args) {
    let url = ''

    // make path
    let urlPathParts = []

    // If the service already defines the path
    // to the request service we have to add
    // only yhe profile and the format to the url
    // eslint-disable-next-line prettier/prettier
    if (args[Constants.propNames.service] && args[Constants.propNames.service].indexOf('http') === 0) {
      url = args[Constants.propNames.service]
      urlPathParts = [
        args[Constants.propNames.profile],
        args[Constants.propNames.format]
      ]
    } else {
      // if not, build the url from scratch
      url = args[Constants.propNames.host]
      urlPathParts = [
        args[Constants.propNames.apiVersion],
        args[Constants.propNames.service],
        args[Constants.propNames.profile],
        args[Constants.propNames.format]
      ]
    }

    let urlPath = '/'
    let counter = 0
    for (let key in urlPathParts) {
      if (urlPathParts[key]) {
        if (counter > 0 && counter) {
          urlPath += '/'
        }
        urlPath += urlPathParts[key]
      }
      counter++
    }

    // Remove double slashs from path
    let cleanUrlPath = urlPath.replace(/\/\//g, '/')
    url += cleanUrlPath

    // The end of the url can not be a slash
    if (url.slice(-1) === '/') {
      url = url.slice(0, -1)
    }
    return url
  }

  /**
   * Set defaults for a request comparing and posibly overwritting instance args
   * @param {Object} instanceArgs
   * @param {Object} requestArgs
   * @param {Boolean} setAPIVersion
   */
  setRequestDefaults(instanceArgs, requestArgs, setAPIVersion = false) {
    if (requestArgs[Constants.propNames.service]) {
      // eslint-disable-next-line prettier/prettier
      instanceArgs[Constants.propNames.service] = requestArgs[Constants.propNames.service]
    }
    if (requestArgs[Constants.propNames.host]) {
      // eslint-disable-next-line prettier/prettier
      instanceArgs[Constants.propNames.host] = requestArgs[Constants.propNames.host]
    }
    if (!instanceArgs[Constants.propNames.host]) {
      instanceArgs[Constants.propNames.host] = Constants.defaultHost
    }
    if (setAPIVersion === true) {
      if (!requestArgs[Constants.propNames.apiVersion]) {
        requestArgs.api_version = Constants.defaultAPIVersion
      }
      if (!requestArgs[Constants.propNames.apiVersion]) {
        requestArgs.api_version = Constants.defaultAPIVersion
      }
    }
  }
}

export default OrsUtil

class OrsInput {
  constructor(input, input2) {
    this.setCoord(input, input2)
  }

  round(val, precision) {
    if (precision === undefined) precision = 1e6
    return Math.round(val * precision) / precision
  }

  setCoord(lat, lng) {
    this.coord = [this.round(lat), this.round(lng)]
  }
}

export default OrsInput

